if ( msg.src === 'restore' ) {
    devs = flow.get("sh2dz-status") || {};
    return [{payload:Object.values(devs)}];
} else {
    if (msg.source === 'gen1') {
        if (msg.topic.split("/")[1] === "announce" || msg.payload === "announce") {
            node.log("Announce message received")
        } else {
            // properties that should  show up in the status
            let inStatus = [];

            // properties that should not show up in the status
            let notStatus = ["name", "status"];

            //node.warn(msg.payload);
            devs = flow.get("sh2dz-status") || {};

            let devt = flow.get("sh2dz-idx") || {};
            let length = Object.keys(devt).length;

            let receivedSensors = [];
            let sw;
            if (msg.topic.split("/")[3] === undefined) {
                sw = msg.topic.split("/")[2];
            } else {
                sw = msg.topic.split("/")[2] + " " + msg.topic.split("/")[3];
            }
            let ID = (msg.topic.split("/")[1]);
            let dev = devs[ID] || {};

            dev.name = msg.topic.split("/")[1];
            dev.sw = sw;
            dev.output = msg.payload;
            //dev.power = msg.payload.apower;
            //dev.voltage = msg.payload.voltage;
            //dev.current = msg.payload.current;

            dev.lastTime = new Date().getTime();

            // create status from properties
            let status = "";
            for (let prop in dev) {
                if (inStatus.includes(prop)) {
                    status += " " + prop + ": " + dev[prop];
                }
            }
            if (status === "") {
                for (let prop in dev) {
                    if (!notStatus.includes(prop)) {
                        status += " " + prop + ": " + dev[prop];
                    }
                }
            }
            dev.status = status;

            // write back
            devs[ID] = dev;
            receivedSensors.push(dev);

            Object.entries(devs).forEach(element => element[1].seen = Math.round((new Date().getTime() - element[1].lastTime) / 60000) + "m");

            flow.set("sh2dz-status", devs);

            return [{ topic: status, payload: Object.values(devs) }];
        }
    } else if (msg.source === 'gen2') {
        if (msg.topic.split("/")[1] === "announce" || msg.payload.output === "announce" ) {
            node.log("Announce message received")
        } else {
            // properties that should  show up in the status
