            let dev = devs[ID] || {};

            dev.name = msg.topic.split("/")[1];
            dev.sw = msg.topic.split(":")[1];
            dev.output = msg.payload.output;
            dev.power = msg.payload.apower;
            dev.voltage = msg.payload.voltage;
            dev.current = msg.payload.current;

            dev.lastTime = new Date().getTime();    

            // create status from properties
            let status = "";
            for (let prop in dev) {
                if (inStatus.includes(prop)) {
                    status += " " + prop + ": " + dev[prop];
                }
            }
            if (status === "") {
                for (let prop in dev) {
                    if (!notStatus.includes(prop)) {
                        status += " " + prop + ": " + dev[prop];
                    }
                }
            }
            dev.status = status;

            // write back
            devs[ID] = dev;
            receivedSensors.push(dev);

            Object.entries(devs).forEach(element => element[1].seen = Math.round((new Date().getTime()-element[1].lastTime)/60000)+"m");

            flow.set("sh2dz-status", devs);

