    if (dev.offline === "Not set" || dev.offline === "Disable") {
      node.log("There is a device which has no heartbeat set or disabled" )  
    } else {
      if (d.getTime() - dev.lasttime > (dev.offline * 1000)) {
        idx = Number(IDkey.split(":")[1])
        let domoticz = flow.get("sh2dz-idx") || {};
        let objlength = Object.keys(domoticz).length;

        for (let j = 0; j < objlength; j++) {
          let objkeys = Object.keys(domoticz);
          let objIDkey = objkeys[i];
          let message = domoticz[objIDkey];
          let messageidx = parseInt(domoticz[objIDkey].idx);

          if (messageidx === idx) {
            if (dev.online === true){
              //apower
              if (Number(message.idxoption1) === 0 || message.idxoption1 === undefined || message.idxoption1 === "N/A" || message.idxoption1 === "Not set") {
                apower = null
              } else {
                apower = {
                  topic: "domoticz/in",
                  payload:
                  {
                    command: "udevice",
                    idx: Number(message.idxoption1),
                    svalue: "-1",
                  }
                }
              }

              //voltage
              if (Number(message.idxoption2) === 0 || message.idxoption2 === undefined || message.idxoption2 === "N/A" || message.idxoption2 === "Not set") {
                voltage = null;
              } else {
                voltage = {
                  topic: "domoticz/in",
                  payload:
                  {
                    command: "udevice",
                    idx: Number(message.idxoption2),
                    svalue: "-1",
                  }
                }
              }

              //current
              if (Number(message.idxoption3) === 0 || message.idxoption3 === undefined || message.idxoption3 === "N/A" || message.idxoption3 === "Not set") {
                current = null;
              } else {
                current = {
                  topic: "domoticz/in",
                  payload:
                  {
                    command: "udevice",
                    idx: Number(message.idxoption3),
                    svalue: "-1",
                  }
                }
              }

              //total
              if (Number(message.idxoption4) === 0 || message.idxoption4 === undefined || message.idxoption4 === "N/A" || message.idxoption4 === "Not set") {
                total = null;
              } else {
                total = {
                  topic: "domoticz/in",
                  payload:
                  {
                    command: "udevice",
                    idx: Number(message.idxoption4),
                    nvalue: 0,
                    svalue: "0;0",
                  }
                }
              }

              //Output
              if (Number(message.idx) === 0 || message.idx === undefined) {
                msg = null
              } else {
                msg = {
                  topic: "domoticz/in",
                  payload:
                  {
                    command: "udevice",
                    idx: Number(message.idx),
                    nvalue: 0,
                  }
                }

                dev.online = false
                dev.output = false

                heartbeat[IDkey] = dev;
                idxstatus.push(dev);

                flow.set("dz-state", heartbeat);
              }
              return [[msg, apower, voltage, current]];
            }
          }
        }
      }  
    }
  }
  return;
}

